import React from 'react'
import { Link, useParams } from 'react-router-dom'
import { schools } from '../data';

const Classe = () => {
  let {i, id, idx}= useParams()
  const title = schools[parseInt(i)].levels[parseInt(idx)].name + schools[parseInt(i)].levels[parseInt(idx)].classes[id];
  const classe = schools[parseInt(i)].levels[parseInt(idx)].content
  return (
    <div className='mx-4  md:mx-8 lg:mx-10'>
        <h1 className='text-center text-3xl font-bold text-blue-900 my-4'>
         {title}
        </h1><hr/>

        <div>
            <table className='w-11/12 border-[1px]'>
              <tr>
                <th className='text-left'> # </th>
                <th className='text-left'> Courses </th>
                <th className='text-left' colSpan={2}> actions </th>
              </tr>
              {
                classe.courses ?

                classe.courses.map((course, index) => (

                        <tr className='m-3 p-2'>
                            <td> {index + 1 } </td>
                            <td> {course.title} </td>
                            <td className='px-4 py-1 text-white bg-blue-600 rounde text-center font-bold'> <Link to={`/`}> Ouvrir </Link> </td>
                        </tr>

                )):
                ''
              }
            </table>
        </div>
    </div>
  )
}

export default Classe
